// Jest 설정 파일
// 테스트 실행 전에 필요한 설정들을 여기에 추가

// 콘솔 경고 억제 (필요한 경우)
const originalWarn = console.warn;
console.warn = (...args) => {
  // 특정 경고 메시지 필터링
  if (args[0] && typeof args[0] === 'string') {
    // libxmljs2 관련 경고 등을 필터링할 수 있음
    if (args[0].includes('libxmljs2')) {
      return;
    }
  }
  originalWarn.apply(console, args);
};

